/**
 * @module CoreServicesModule
 **/
/** AppConfigInterface **/
/**
 * The SessionServiceConfig
 * @class SessionServiceConfig
 **/
export interface SessionServiceConfig {
    sessionTimeout: number;
    keepAliveInterval: number;
    sessionKeepAliveUrl: string;
    sessionCookies: string[];
}
/**
 * The HttpServiceConfig
 * @class HttpServiceConfig
 **/
export interface HttpServiceConfig {
    defaultHeaders: [{
        headerName: string;
        headerValue: string;
    }];
    headersToSendFromPreviousResponse: [{
        headerNameInPreviousResponse: string;
        headerNameToSendInRequest: string;
    }];
    sendSessionCookieValuesInRequestHeaders: boolean;
    cookieValuesHeaders: [{
        headerName: string;
        headerValue: string;
    }];
}
/**
 * The ContentServiceConfig
 * @class ContentServiceConfig
 **/
export interface ContentServiceConfig {
    staticBaseUrl: string;
    bundles: {
        [key: string]: {
            businessId: string;
            locale: string;
        };
    };
}
/**
 * The AppConfig
 * @class AppConfig
 **/
export interface AppConfig {
    apiServerUrl: string;
    sessionService: SessionServiceConfig;
    httpService: HttpServiceConfig;
    contentService: ContentServiceConfig;
    customConfig: any;
}
