import { Component, Input } from '@angular/core';

@Component({
  selector: 'citi-action-contact-us',
  template: require('./action-contact-us.component.html'),
  styles: [require('./action-contact-us.component.css')]
})

export class ActionContactUsComponent {
  
  /*
   * @Property: headerLevel
   * @Description: This will create the requested HTML header tag <h1> - <h6>
   * @Type: string
   */
  @Input() headerLevel: string;
  
  /*
   * @Property: headerText
   * @Description: Text placed within the header tag pair
   * @Type: string
   */
  @Input() headerText: string;
  
  /*
   * @Property: imageAlt
   * @Description: Text placed within the alt attribute
   * @Type: string
   */
  @Input() imageAlt: string;
  
  /*
   * @Property: imageSrc 
   * @Description: Image source url. If not supplied, the image won't display 
   * @Type: string
   */
  @Input() imageSrc: string;
  
  /*
   * @Property: linkText
   * @Description: Diplayed link text
   * @Type: string
   */
  @Input() linkText: string;
  
  /*
   * @Property: url
   * @Description: URL for link
   * @Type: string
   */
  @Input() url: string;
  
  constructor() { }

}