import { Component, Input } from '@angular/core';

@Component({
  selector: 'citi-action-link',
  template: require('./action-link.component.html'),
  styles: [require('./action-link.component.css')]
})

export class ActionLinkComponent {

  /*
   * @Property: linkDescription
   * @Description: Description text which is displayed beneath the link
   * @Type: string
   */
  @Input() linkDescription: string;
  
  /*
   * @Property: linkText
   * @Description: Displayed link text
   * @Type: string
   */
  @Input() linkText: string;
  
  /*
   * @Property: linkUrl
   * @Description: Destination URL for link
   * @Type: string
   */
  @Input() linkUrl: string;
  
  constructor() { }

}