import { Component, OnInit, Input } from '@angular/core';

@Component({
  selector: 'citi-alert',
  template: require('./alert.component.html')
})
export class AlertComponent implements OnInit {

  private alertClass = 'alert';
  private criticalClass = 'critical';
  private alertBoxClass = 'alert-box';
  private cssClass:string;

  private alertType: 'alert' | 'critical' | 'alertBox' = 'alert';

  @Input()
  set type(type:any) {
    this.alertType = type;
  }

  @Input() notice:boolean = false;

  @Input() noticeText:string;

  @Input() alertText:string = 'Alert!';

  constructor() { }

  ngOnInit() {

    switch(this.alertType) {
      case 'alert':
        this.cssClass = this.alertClass;
        break;
      case 'critical':
        this.cssClass = this.alertClass + ' ' + this.criticalClass;
        break;
      case 'alertBox':
        this.cssClass = this.alertBoxClass;
        break;
    }
  }

}
