import { Component, OnInit, Input } from '@angular/core';

/**
 * @Component: Card figure
 * @Description: This component creates a single figure for feature lists
 */

@Component({
  selector: 'citi-card-figure',
  templateUrl: './figure.component.html',
  styleUrls: ['./figure.component.css']
})
export class FigureComponent implements OnInit {

  /**
   * @Property: label
   * @Description: Label to show above the figure/item
   * @Type: String
   */
  @Input() label:string;

  /**
   * @Property: footer
   * @Description: Whether this is the last figure in a citi-card-features column
   * @Type: Boolean
   */
  @Input() footer:any;

  private footerClass;

  constructor() { }

  ngOnInit() {
    this.footerClass = (this.footer ? "footer" : "");
  }

}
