import { Component, OnInit, ViewChild, ElementRef } from '@angular/core';

/**
 * @Component: Card highlights
 * @Description: This component creates a single column of card highlights for the C2.X DDL modules
 */

@Component({
  selector: 'citi-card-highlight',
  templateUrl: './highlight.component.html',
  styleUrls: ['./highlight.component.css']
})
export class HighlightComponent implements OnInit {

  // Does this module have a "callout" to display
  private hasCallout:boolean = true;

  // Reference to the "callout" child element, so it can be hidden when no content is passed
  @ViewChild('callout') callout: ElementRef;

  constructor() { }

  ngOnInit() {
    this.hasCallout = this.callout.nativeElement.childNodes.length !== 0;
  }

}
