import { Component, OnInit, Input, OnDestroy, HostBinding } from '@angular/core';
import { Carousel, Direction } from './carousel.component';

@Component({
    selector: 'slide',
    template: `
    <div [class.active]="active" class="item text-center">
      <ng-content></ng-content>
    </div>
  `
})
export class CarouselSlide implements OnInit, OnDestroy {
    @Input() public index:number;
    /* direction is about prev and next */
    @Input() public direction:Direction;

    @HostBinding('class.active')
    @Input() public active:boolean;
    
    @HostBinding('class.item')
    @HostBinding('class.carousel-item')
    private addClass:boolean = true;

    constructor(private carousel:Carousel) {
    }

    public ngOnInit() {
        this.carousel.addSlide(this);
    }

    public ngOnDestroy() {
        //this.carousel.removeSlide(this);
    }
}