import { Component, Input, ElementRef } from '@angular/core';

@Component({
    selector: 'citi-accordion-section',
    template: require('./accordion-section.component.html')
})
export class AccordionSectionComponent {

    // Create a reference to this element
    constructor(private element: ElementRef){};

    // Will hold the index of this accordion section
    index: Number;

    ngOnInit() {

        // Get all of the sections in this accordion
        var sections = this.element.nativeElement.parentElement.getElementsByTagName("citi-accordion-section");
        
        // Convert the HTMLCollection into an array so we can use indexOf
        sections = [].slice.call(sections);

        // Set the index of this section
        this.index = sections.indexOf(this.element.nativeElement);
    }

    @Input() title: string;

    @Input() description: string;
}