import { Component, Input } from '@angular/core';

/**
 * @Component: Sub Content Block
 * @Description: This component generates a sub-content block (B3.0)
 */
@Component({
    selector: 'citi-sub-content-block',
    template: require('./sub-content.component.html'),
    styles: [ require('./sub-content.component.scss') ]
})

export class SubContentComponent {
    /**
     * @Property: title
     * @Description: The title for the sub content block
     * @Type: String
     */
    @Input() title: string;

    /**
     * @Property: description
     * @Description: The description for the sub content block
     * @Type: String
     */
    @Input() description: string;

    /**
     * @Property: color
     * @Description: The color for the sub content block. Default is gray. Can be passed "blue".
     * @Type: String
     */
    @Input() color: string;
}