import { Component, OnInit, Input } from '@angular/core';

/**
 * @Component: Datepicker
 * @Description: The datepicker component.
 */
@Component({
  selector: 'citi-datepicker',
  template: require('./datepicker.component.html'),
  styles: [ require('./datepicker.component.css') ]
})
export class DatepickerComponent implements OnInit {

  /**
  * @Property: viewFormat
  * @Description: This property allows you to change the format of the date to be displayed in the input field 
  *               default format is 'dddd MMMM DD, YYYY'
  * @Type: String (Format: 'MM-DD-YYYY','DD-MM-YYY' etc.) 
  */
  @Input() viewFormat: string;

  /**
  * @Property: prePopulateDate
  * @Description: This property allows you to pre-populate date in the calendar
  * @Type: String (Format: 'MM-DD-YYYY') 
  */
  @Input() prePopulateDate: string;

  constructor() { }

  ngOnInit() {
  }

}
