import { Component, OnInit, Input } from '@angular/core';

/**
 * @Component: Date of Birth
 * @Description: This is the DOB component and is used to create reusable DOB
 * from Bootstrap.
*/
@Component({
  selector: 'citi-dob-control',
  template: require('./dob-control.component.html'),
  styles: [ require('./dob-control.component.css') ]
})
export class DobControlComponent implements OnInit {
 
  /**
    * @Property: idStr 
    * @Description: This defines the id of the date of birth control
    * @Type: string 
   */
  @Input() idStr:string;   
  /**
    * @Property: dobLabel 
    * @Description: This defines the label value  of the date of birth control
    * @Type: string 
   */
  @Input() dobLabel:string;  
  /**
    * @Property: monthOptions 
    * @Description: This defines the month of the date of birth control
    * @Type: Object
   */
  @Input() monthOptions:Object;
  /**
    * @Property: dayOptions 
    * @Description: This defines the day of the date of birth control
    * @Type: Object
   */ 
  @Input() dayOptions:Object;
   /**
    * @Property: yearOptions 
    * @Description: This defines the year of the date of birth control
    * @Type: Object
   */ 
  @Input() yearOptions:Object;   
  /**
    * @Property: monthLabel 
    * @Description: This defines the Month label of the date of birth control
    * @Type: string 
   */
  @Input() monthLabel:string;
   /**
    * @Property: dayLabel 
    * @Description: This defines the day label of the date of birth control
    * @Type: string 
   */
  @Input() dayLabel:string;
  /**
    * @Property: yearLabel 
    * @Description: This defines the year label of the date of birth control
    * @Type: string 
   */
  @Input() yearLabel:string;   
   /**
    * @Property: dobOrder 
    * @Description: This defines the order in which the Month, Day, and Year select controls appear.
    * @Type: string (MDY, YMD, DMY)
   */
  @Input() dobOrder:string;  
   /**
    * @Property: isMasked 
    * @Description: This defines the Controls whether or not the date of birth field components will be masked once a value is selected.
    * @Type: boolean
   */
  @Input() isMasked:boolean;



  constructor() { }

  ngOnInit() {    
  }

}
