import { EventEmitter } from "@angular/core";

export class InputService {

    public inputSelected: EventEmitter<string>;
    public inputHasError: EventEmitter<boolean>;
    private error: boolean = false;

    constructor() {
        this.inputSelected = new EventEmitter<"">();
        this.inputHasError = new EventEmitter<false>();
    }

    setInputError: Function = function( error: boolean ){

        this.error = error;
        this.inputHasError.emit(this.error);
    }

    hasInputError: Function = function(){
        
        return this.error;
    }
}