import { NgModule, SkipSelf } from '@angular/core';

import { CitiModule } from '../citi.module'
import { LeftNavLayoutComponent } from './leftnav/leftnav-layout.component';
import { SimpleLayoutComponent } from './simple/simple-layout.component';

@NgModule({
    imports: [],
    declarations: [
        SimpleLayoutComponent,
        LeftNavLayoutComponent
    ],
    exports: [
        SimpleLayoutComponent,
        LeftNavLayoutComponent
    ]
})

export class LayoutsModule {
    constructor (@SkipSelf() parentModule: CitiModule) {
        if (parentModule) {
            throw new Error(
            'CitiModule is already loaded. Import it in the AppModule only');
        }
    }
}