import { Component, OnInit, Input } from '@angular/core';

/**
 * @Component: Left Nav Child
 * @Description: This is first level child component inside the parent component. 
*/
@Component({
  selector: '.citi-left-nav-child',
  template: require('./left-nav-child.component.html'),
  styles: [ require('./left-nav-child.component.css') ]
})
export class ChildComponent implements OnInit {

  isExpanded: boolean = false;
 
  // Custom Attributes
  @Input() hasChildren: Boolean = false;
  @Input() title: String;
  @Input() href: String;
  @Input() subMenuList: any[];

  constructor() { }

  // When span clicked
  isClicked(event){
    this.isExpanded = !this.isExpanded;
  }

  /**
  * @Function: logClick
  * @Description: This function stops propagating the event in the DOM hierarchy.
  * @Param: (String) - event - Event object.
  * @Param: (String) - index - Index of clicked nav item.
  * @Return: -
*/
  logClick(event, index){
      event.stopPropagation();
  }

  ngOnInit() { }

}
