
enum StepState {
  Active,
  Finished,
  Normal
}

export class Step {

  private state: StepState = StepState.Normal;
  private text: string;

  constructor(text, state: StepState = StepState.Normal){
    this.setText(text);
  }

  public getText (): string{
    return this.text;
  }

  public getState (): StepState{
    return this.state;
  }

  public setText(text: string) {
    this.text = text;
  }

  public setState(state: StepState = StepState.Normal){
    this.state = state;
  }

  public setActive(){
    this.state = StepState.Active;
  }

  public setFinished(){
    this.state = StepState.Finished;
  }

  public setNormal(){
    this.state = StepState.Normal;
  }

  public isActive(){
    return this.state === StepState.Active;
  }

  public isFinished(){
    return this.state === StepState.Finished;
  }
}
