import { Component, Input, ViewChild, ElementRef, AfterContentChecked } from '@angular/core';

/**
 * @Component: Terms and Conditions
 * @Description: This component generates a terms and conditions section
 */
@Component({
    selector: 'citi-terms-and-conditions',
    template: require('./terms.component.html')
})

export class TermsAndConditionsComponent implements AfterContentChecked {

    // A reference to the ngContent div
    @ViewChild("content") content: ElementRef;

    // String to hold the content html
    contentHtml: string;

    // Once all of the content is available
    ngAfterContentChecked(){

        // Get the innerHTML
        var innerHtml = this.content.nativeElement.innerHTML.toString();

        // If we got anything back
        if ( innerHtml.length ){

            // Set the content HTML string
            this.contentHtml = innerHtml;
        }
    }

    /**
     * @Property: type
     * @Description: Type of terms and condition block to create. Valid types are 'standalone', 'scrollbox', and 'modal'
     * @Type: string
     */
    @Input() type: string;
}